import BottomButtons from './BottomButtons.vue'
const requirements = ref([
    {
        id : 0,
        value : '',
        freeText : true,
        fileUpload : false,
        required : false,
    }
])
const addNewRequirement = () =>{
    requirements.value.push(
        {
            id : requirements.value.length,
            value : '',
            freeText : true,
            fileUpload : false,
            required : false,
        }
    )
}
const deleteRequirement = (id) => {
    requirements.value = requirements.value.filter(e => e.id !== id)
    for(let i = 0 ; i < requirements.value.length ; i++){
        requirements.value[i].id = i
    }
}
